    if (!taskToUpdate) return;
    const updatedTask = Object.assign({}, taskToUpdate, { status: newStatus });
    setTasks(prev => prev.map(t => String(t.id) === String(id) ? updatedTask : t));
    try {
      await fetch(API_URL + "/" + id, { method: "PUT", headers: { "Content-Type": "application/json" }, body: JSON.stringify(updatedTask) });
      console.log("âœ… Task updated:", updatedTask);
    } catch (err) { console.error("âŒ Error updating task:", err); }
  }

  function tasksByStatus(status) { return tasks.filter(t => t.status === status); }

  function handleOpenModal() { setNewTask({ title: "", message: "", startDate: "", endDate: "" }); setOpenModal(true); }
  function handleCloseModal() { setOpenModal(false); }

  async function handleAddTask() {
    const payload = Object.assign({}, newTask, { status: "open", id: tasks.length + 1 });
    try {
      await fetch(API_URL, { method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify(payload) });
      setTasks(prev => prev.concat(payload));
      setOpenModal(false);
    } catch (err) { console.error("Error adding task:", err); }
  }

