import React, { useEffect, useState } from "react";
import ReactApexChart from "react-apexcharts";

const API_URL = "https://activit.free.beeceptor.com/api/v3/activities";

// Función para convertir fechas
const parseDate = (dateStr) => {
  const [day, month, year] = dateStr.split("-").map(Number);
  return `${year}-${month.toString().padStart(2, "0")}-${day.toString().padStart(2, "0")}`;
};

// Transformar datos para el heatmap
const transformData = (data) => {
  const activityNames = Object.keys(data.activities);
  const uniqueDates = [...new Set(Object.values(data.activities).flatMap((records) => records.map(({ date }) => parseDate(date))))].sort();

  return uniqueDates.map((date) => ({
    name: date,
    data: activityNames.map((activity) => {
      const record = data.activities[activity]?.find((r) => parseDate(r.date) === date);
      let yValue = null;
      if (record) {
        if (record.status === "accomplished") yValue = 1;
        else if (record.status === "failed") yValue = 0;
        else if (record.status === "regular") yValue = 0.5;
      }
      return { x: activity, y: yValue, metadata: { date, activity, record } };
    }),
  }));
};

const ApexChart = () => {
  const [chartData, setChartData] = useState({ series: [], options: {} });
  const [modalData, setModalData] = useState(null);
  const [selectedStatus, setSelectedStatus] = useState("");

  useEffect(() => {
    fetch(API_URL)
      .then((response) => response.json())
      .then((data) => {
        const series = transformData(data);
        setChartData({
          series,
          options: {
            chart: {
              height: 9000,
              type: "heatmap",
              events: {
                dataPointSelection: (event, chartContext, { dataPointIndex, seriesIndex }) => {
                  const selectedPoint = series[seriesIndex].data[dataPointIndex];
                  setModalData(selectedPoint.metadata);
                },
              },
            },
            plotOptions: {
              heatmap: {
                shadeIntensity: 0.5,
                radius: 0,
                useFillColorAsStroke: true,
                colorScale: {
                  ranges: [
                    { from: 0, to: 0, name: "Failed", color: "#FF0000" },
                    { from: 1, to: 1, name: "Accomplished", color: "#00A100" },
                    { from: 0.5, to: 0.5, name: "Regular", color: "#FFFF00" },
                  ],
                },
              },
            },
            dataLabels: { enabled: false },
            title: { text: "Activity HeatMap" },
            xaxis: { type: "category", title: { text: "Activities" } },
            yaxis: { title: { text: "Dates" } },
          },
        });
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handleSave = () => {
    console.log("Saving status:", modalData, selectedStatus);
    setModalData(null);
  };

  return (
    <div>
      <h2>Activity Heatmap</h2>
      <ReactApexChart options={chartData.options} series={chartData.series} type="heatmap" height={9000} />

      {modalData && (
        <div style={{
          position: "fixed",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          background: "white",
          padding: "20px",
          borderRadius: "8px",
          boxShadow: "0px 0px 10px rgba(0,0,0,0.2)"
        }}>
          <h3>Update Status</h3>
          <p>
            Activity: <strong>{modalData.activity}</strong>
          </p>
          <p>
            Date: <strong>{modalData.date}</strong>
          </p>
          <select value={selectedStatus} onChange={(e) => setSelectedStatus(e.target.value)}>
            <option value="">Select status</option>
            <option value="accomplished">Accomplished</option>
            <option value="failed">Failed</option>
            <option value="regular">Regular</option>
          </select>
          <div style={{ marginTop: "10px" }}>
            <button onClick={handleSave} style={{ marginRight: "10px", padding: "5px 10px", cursor: "pointer" }}>
              Save
            </button>
            <button onClick={() => setModalData(null)} style={{ padding: "5px 10px", cursor: "pointer" }}>
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ApexChart;
