import React from "react";
import Chart from "react-apexcharts";

const WeightChart = () => {
  const data = {
    ideal: [
      { weight: 84, date: "01-01-2025" },
      { weight: 75, date: "31-12-2025" }
    ],
    current: [
      { weight: 84, date: "01-01-2025" },
      { weight: 83, date: "01-02-2025" },
      { weight: 82, date: "01-03-2025" },
      { weight: 81, date: "01-04-2025" },
      { weight: 80, date: "01-05-2025" },
      { weight: 79, date: "01-06-2025" },
      { weight: 78, date: "01-07-2025" },
      { weight: 77, date: "01-08-2025" },
      { weight: 76, date: "01-09-2025" },
      { weight: 75, date: "01-10-2025" },
      { weight: 74, date: "01-11-2025" },
      { weight: 73, date: "01-12-2025" }
    ]
  };

  const options = {
    chart: {
      id: "weight-chart",
      type: "line",
      zoom: { enabled: false }
    },
    xaxis: {
      categories: data.current.map(d => d.date),
      title: { text: "Fecha" }
    },
    yaxis: {
      title: { text: "Peso (kg)" }
    },
    stroke: {
      curve: "smooth"
    },
    markers: {
      size: 4
    },
    legend: {
      position: "top"
    }
  };

  const series = [
    {
      name: "Ideal",
      data: [
        { x: data.ideal[0].date, y: data.ideal[0].weight },
        { x: data.ideal[1].date, y: data.ideal[1].weight }
      ]
    },
    {
      name: "Actual",
      data: data.current.map(d => ({ x: d.date, y: d.weight }))
    }
  ];

  return (
    <div className="p-4 bg-white rounded-xl shadow">
      <h2 className="text-xl font-semibold mb-2">Progreso de Peso</h2>
      <Chart options={options} series={series} type="line" height={350} />
    </div>
  );
};

export default WeightChart;
