import React, { useEffect, useState } from "react";
import ReactApexChart from "react-apexcharts";

const API_URL = "http://localhost:8080/activities/grouped";

const parseDate = (dateStr) => {
  const [day, month, year] = dateStr.split("-").map(Number);
  return `${year}-${month.toString().padStart(2, "0")}-${day.toString().padStart(2, "0")}`;
};

const transformData = (data) => {
  const activityNames = Object.keys(data.activities);
  const uniqueDates = [...new Set(Object.values(data.activities).flatMap((records) => records.map(({ date }) => parseDate(date))))].sort();

  return activityNames.map((activity) => ({
    name: activity,
    data: uniqueDates.map((date) => {
      const record = data.activities[activity]?.find((r) => parseDate(r.date) === date);
      let yValue = null;
      if (record) {
        const status = (record.status || "").toLowerCase();
        if (status === "accomplished") yValue = 1;
        else if (status === "failed") yValue = 0.2;
        else if (status === "regular") yValue = 0.5;
        else if (status === "suck") yValue = 0.001;
        else if (status === "excellence") yValue = 1.2;
      }
      return { x: date, y: yValue };
    }),
  }));
};

const ApexChartMobile = () => {
  const [chartData, setChartData] = useState({ series: [], options: {} });

  useEffect(() => {
    fetch(API_URL)
      .then((response) => response.json())
      .then((data) => {
        const series = transformData(data);

        setChartData({
          series,
          options: {
            chart: {
              type: "heatmap",
              height: "auto",
              toolbar: { show: false },
            },
            plotOptions: {
              heatmap: {
                shadeIntensity: 0.5,
                radius: 0,
                useFillColorAsStroke: false,
                colorScale: {
                  ranges: [
                    { from: 0.001, to: 0.001, name: "Suck", color: "#000000" },
                    { from: 0.2, to: 0.2, name: "Failed", color: "#FF0000" },
                    { from: 0.5, to: 0.5, name: "Regular", color: "#FFFF00" },
                    { from: 1, to: 1, name: "Accomplished", color: "#00A100" },
                    { from: 1.2, to: 1.2, name: "Excellence", color: "#0000FF" },
                    { from: null, to: null, name: "No Status", color: "#FFFFFF" },
                  ],
                },
              },
            },
            dataLabels: {
              enabled: false,
            },
            xaxis: {
              type: "category",
              position: "top", // <-- Hace que los labels de las actividades aparezcan arriba
              labels: {
                rotate: -45,
                style: {
                  fontSize: "12px",
                  fontWeight: 600,
                  fontFamily: "Roboto, sans-serif",
                },
              },
            },
            yaxis: {
              title: { text: "Días" },
              labels: {
                style: {
                  fontSize: "12px",
                  fontWeight: 600,
                  fontFamily: "Roboto, sans-serif",
                },
              },
              opposite: false, // Fechas a la izquierda
            },
            grid: {
              padding: { right: 5, left: 5 },
            },
            title: {
              text: "Activity Heatmap",
              style: {
                fontSize: "16px",
                fontWeight: "bold",
                fontFamily: "Roboto, sans-serif",
              },
            },
          },
        });
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div style={{ marginTop: "1rem", padding: "1rem" }}>
      <ReactApexChart
        options={chartData.options}
        series={chartData.series}
        type="heatmap"
        height={600}
        width="100%"
      />
    </div>
  );
};

export default ApexChartMobile;
