// ... mismo imports y funciones ...

const StatusModal = ({ selectedCell, setSelectedCell, refreshData }) => {
  if (!selectedCell) return null;

  const currentValue = statusLevels.find((s) => s.label === selectedCell.status)?.value || 0.55;

  const handleStatusChange = (_, newValue) => {
    const closestStatus = getClosestStatus(newValue);
    setSelectedCell((prev) => ({ ...prev, status: closestStatus.label }));
  };

  const handleSave = () => {
    const closestStatus = getClosestStatus(currentValue);
    const formattedDate = formatDate(selectedCell.x);

    fetch(UPDATE_API_URL, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        date: formattedDate,
        activity: selectedCell.activity,
        status: closestStatus.label,
      }),
    })
      .then(() => {
        console.log("Updated successfully");
        refreshData();
      })
      .catch((err) => console.error("Error updating:", err));

    setSelectedCell(null);
  };

  const closestStatus = getClosestStatus(currentValue);

  return (
    <Modal open={!!selectedCell} onClose={() => setSelectedCell(null)}>
      <Box
        sx={{
          p: 2,
          bgcolor: "#FFFFFF",
          borderRadius: 1,
          boxShadow: 10,
          maxWidth: 420,
          margin: "auto",
          display: "flex",
          flexDirection: "column",
          fontFamily: "Arial, sans-serif",
        }}
      >
        {/* Slider */}
        <Slider
          value={currentValue}
          step={0.01}
          min={0.15}
          max={0.95}
          onChange={handleStatusChange}
          sx={{
            color: closestStatus.color,
            '& .MuiSlider-markLabel': {
              fontSize: 0,
            },
            mt: 0,
            mb: 1,
          }}
        />

        {/* Button */}
        <Box
          sx={{
            display: "flex",
            justifyContent: "flex-end",
            mb: 0.5,
          }}
        >
          <Button
            variant="contained"
            onClick={handleSave}
            sx={{
              fontSize: "0.95rem",
              fontWeight: "bold",
              px: 2,
              py: 0.8,
              textTransform: "none",
              bgcolor: "#000",
              '&:hover': { bgcolor: "#222" },
              height: "fit-content",
            }}
          >
            Save
          </Button>
        </Box>

        {/* Dynamic label (now under the button) */}
        <Box sx={{ display: "flex", justifyContent: "center", mb: 1 }}>
          <Typography
            variant="body1"
            sx={{
              fontSize: "1.1rem",
              fontWeight: "bold",
              color: "#000000",
            }}
          >
            {closestStatus.label}
          </Typography>
        </Box>

        {/* Info */}
        <Box>
          <Typography variant="body2" sx={{ fontSize: "1rem", color: "#000" }}>
            <strong>Date:</strong> {formatDate(selectedCell.x)}
          </Typography>
          <Typography variant="body2" sx={{ fontSize: "1rem", color: "#000" }}>
            <strong>Activity:</strong> {selectedCell.activity}
          </Typography>
        </Box>
      </Box>
    </Modal>
  );
};

export default StatusModal;
