import React, { useEffect, useState } from "react";
import Chart from "react-apexcharts";

// Interpolaci칩n con null fuera del rango v치lido
const interpolateWithNullsOutside = (data, allDates) => {
  const result = [];
  const start = new Date(data[0].date).getTime();
  const end = new Date(data[data.length - 1].date).getTime();

  allDates.forEach(date => {
    const dateMs = new Date(date).getTime();

    if (dateMs < start || dateMs > end) {
      result.push({ x: dateMs, y: null });
      return;
    }

    let i = 0;
    while (i < data.length && new Date(data[i].date).getTime() < dateMs) {
      i++;
    }

    if (i === 0) {
      result.push({ x: dateMs, y: data[0].weight });
    } else if (i === data.length) {
      result.push({ x: dateMs, y: data[data.length - 1].weight });
    } else {
      const prev = data[i - 1];
      const next = data[i];
      const t = (dateMs - new Date(prev.date).getTime()) / (new Date(next.date).getTime() - new Date(prev.date).getTime());
      const y = prev.weight + t * (next.weight - prev.weight);
      result.push({ x: dateMs, y });
    }
  });

  return result;
};

const WeightChart = () => {
  const [chartData, setChartData] = useState({ ideal: [], current: [] });
  const [newWeight, setNewWeight] = useState("");
  const [newDate, setNewDate] = useState("");

  useEffect(() => {
    fetch("https://weight.free.beeceptor.com/weights")
      .then((res) => res.json())
      .then((data) => setChartData(data))
      .catch((err) => console.error("Error fetching data:", err));
  }, []);

  if (!chartData.ideal.length || !chartData.current.length) return <p>Cargando...</p>;

  const allDates = Array.from(
    new Set([
      ...chartData.ideal.map(d => d.date),
      ...chartData.current.map(d => d.date)
    ])
  )
    .map(d => new Date(d))
    .sort((a, b) => a - b)
    .map(d => d.toISOString().slice(0, 10));

  const series = [
    {
      name: "Ideal",
      data: interpolateWithNullsOutside(chartData.ideal, allDates)
    },
    {
      name: "Actual",
      data: interpolateWithNullsOutside(chartData.current, allDates)
    }
  ];

  const options = {
    chart: {
      id: "weight-chart",
      type: "line",
      zoom: { enabled: false },
      toolbar: { show: false }
    },
    xaxis: {
      type: "datetime",
      title: { text: "Fecha" },
      labels: {
        formatter: (value) => {
          const date = new Date(value);
          return `${date.getDate()} ${date.toLocaleString("default", { month: "short" })} ${date.getFullYear()}`;
        }
      }
    },
    yaxis: {
      title: { text: "Peso (kg)" },
      labels: {
        formatter: (value) => Math.floor(value)
      }
    },
    stroke: {
      curve: "smooth",
      width: 2
    },
    markers: {
      size: 0
    },
    tooltip: {
      shared: true,
      intersect: false,
      x: {
        format: "dd MMM yyyy"
      },
      y: {
        formatter: (val) => (val !== null ? val.toFixed(2) : "-")
      }
    },
    legend: {
      show: true,
      position: "top",
      horizontalAlign: "right"
    },
    colors: ["#008FFB", "#00E396"]
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Nuevo peso:", newWeight, "Fecha:", newDate);
    // L칩gica para enviar a la API si lo necesit치s
  };

  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
      <div style={{
        width: "60%",
        padding: "1.5rem",
        backgroundColor: "white",
        borderRadius: "1rem",
        boxShadow: "0 4px 10px rgba(0,0,0,0.1)"
      }}>
        <form onSubmit={handleSubmit}>
          <div style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            gap: "1rem",
            marginBottom: "1.5rem",
            flexWrap: "wrap"
          }}>
            <div style={{ minWidth: "120px", textAlign: "left" }}>
              <div style={{
                fontSize: "1.2rem",
                color: "#555",
                fontFamily: "'Segoe UI', sans-serif"
              }}>
                Peso Ideal: <strong>75 kg</strong>
              </div>
            </div>

            <div style={{
              display: "flex",
              alignItems: "center",
              gap: "0.75rem",
              flexShrink: 0,
              flexGrow: 1,
              justifyContent: "flex-end"
            }}>
              <div style={{ display: "flex", gap: "1rem", marginRight: "1rem" }}>
                <div style={{ display: "flex", alignItems: "center", gap: "0.4rem" }}>
                  <div style={{ width: "14px", height: "14px", backgroundColor: "#008FFB", borderRadius: "50%" }}></div>
                  <span style={{ color: "#444", fontSize: "0.9rem" }}>Ideal</span>
                </div>
                <div style={{ display: "flex", alignItems: "center", gap: "0.4rem" }}>
                  <div style={{ width: "14px", height: "14px", backgroundColor: "#00E396", borderRadius: "50%" }}></div>
                  <span style={{ color: "#444", fontSize: "0.9rem" }}>Actual</span>
                </div>
              </div>

              <input
                type="number"
                value={newWeight}
                onChange={(e) => setNewWeight(e.target.value)}
                placeholder="Nuevo peso (kg)"
                style={{
                  padding: "0.5rem 0.75rem",
                  borderRadius: "0.5rem",
                  border: "1px solid #ccc",
                  width: "120px",
                  fontSize: "0.9rem"
                }}
              />
              <input
                type="date"
                value={newDate}
                onChange={(e) => setNewDate(e.target.value)}
                style={{
                  padding: "0.5rem 0.75rem",
                  borderRadius: "0.5rem",
                  border: "1px solid #ccc",
                  fontSize: "0.9rem"
                }}
              />
              <button
                type="submit"
                style={{
                  padding: "0.55rem 1.1rem",
                  borderRadius: "0.5rem",
                  border: "none",
                  backgroundColor: "#8e44ad",
                  color: "white",
                  fontWeight: "bold",
                  fontSize: "0.9rem",
                  cursor: "pointer"
                }}
              >
                Agregar
              </button>
            </div>
          </div>
        </form>

        <Chart options={options} series={series} type="line" height={320} width="100%" />
      </div>
    </div>
  );
};

export default WeightChart;
