import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom/client";

const STATUS_ORDER = ["open", "blocked", "in_progress", "done"];
const STATUS_LABEL = {
  open: "Open",
  blocked: "Blocked",
  in_progress: "In Progress",
  done: "Done",
};

function normalizeStatus(raw) {
  if (!raw) return "open";
  const r = raw.toLowerCase();
  if (r === "completed" || r === "done") return "done";
  if (r === "in_progress" || r === "in progress") return "in_progress";
  if (r === "block" || r === "blocked") return "blocked";
  if (r === "open") return "open";
  return r;
}

export default function TaskBoard() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [draggingId, setDraggingId] = useState(null);

  useEffect(() => {
    let mounted = true;
    fetch("https://testtask.free.beeceptor.com/todo")
      .then((r) => {
        if (!r.ok) throw new Error(`HTTP ${r.status}`);
        return r.json();
      })
      .then((data) => {
        if (!mounted) return;
        const normalized = data.map((t) => ({
          ...t,
          status: normalizeStatus(t.status),
        }));
        setTasks(normalized);
        setLoading(false);
      })
      .catch((e) => {
        setError(e.message);
        setLoading(false);
      });
    return () => (mounted = false);
  }, []);

  function onDragStart(e, id) {
    e.dataTransfer.setData("text/plain", String(id));
    setDraggingId(id);
  }

  function onDragEnd() {
    setDraggingId(null);
  }

  function allowDrop(e) {
    e.preventDefault();
  }

  function onDrop(e, newStatus) {
    e.preventDefault();
    const id = e.dataTransfer.getData("text/plain");
    if (!id) return;
    setTasks((prev) =>
      prev.map((t) => (String(t.id) === String(id) ? { ...t, status: newStatus } : t))
    );
    setDraggingId(null);
  }

  function tasksByStatus(status) {
    return tasks.filter((t) => t.status === status);
  }

  return React.createElement(
    "div",
    { className: "min-h-screen bg-gray-100 p-6" },
    React.createElement(
      "div",
      { className: "max-w-7xl mx-auto" },
      React.createElement(
        "header",
        { className: "mb-6 flex items-center justify-between" },
        React.createElement("h1", { className: "text-2xl font-semibold" }, "Kanban Board"),
        React.createElement(
          "div",
          { className: "text-sm text-gray-600" },
          "Drag tasks horizontally between columns"
        )
      ),
      loading
        ? React.createElement(
            "div",
            { className: "py-20 text-center text-gray-500" },
            "Cargando tareas..."
          )
        : error
        ? React.createElement(
            "div",
            { className: "py-20 text-center text-red-600" },
            `Error: ${error}`
          )
        : React.createElement(
            "div",
            { className: "grid grid-cols-1 sm:grid-cols-4 gap-4" },
            STATUS_ORDER.map((statusKey) =>
              React.createElement(
                "div",
                {
                  key: statusKey,
                  className:
                    "rounded-lg p-3 border border-gray-200 bg-white flex flex-col max-h-[70vh]",
                  onDragOver: allowDrop,
                  onDrop: (e) => onDrop(e, statusKey),
                },
                React.createElement(
                  "div",
                  { className: "mb-2 flex items-center justify-between" },
                  React.createElement(
                    "h2",
                    { className: "text-sm font-medium" },
                    STATUS_LABEL[statusKey]
                  ),
                  React.createElement(
                    "span",
                    { className: "text-xs text-gray-500" },
                    tasksByStatus(statusKey).length
                  )
                ),
                React.createElement(
                  "div",
                  { className: "overflow-auto space-y-3" },
                  tasksByStatus(statusKey).length === 0
                    ? React.createElement(
                        "div",
                        { className: "text-xs text-gray-400" },
                        "No tasks"
                      )
                    : tasksByStatus(statusKey).map((task) =>
                        React.createElement(
                          "article",
                          {
                            key: task.id,
                            draggable: true,
                            onDragStart: (e) => onDragStart(e, task.id),
                            onDragEnd: onDragEnd,
                            className:
                              `p-3 rounded-lg shadow-sm border cursor-grab select-none bg-white ${
                                draggingId === task.id ? "opacity-60" : "opacity-100"
                              }`,
                          },
                          React.createElement(
                            "div",
                            { className: "flex items-start justify-between" },
                            React.createElement(
                              "h3",
                              { className: "text-sm font-semibold" },
                              task.title
                            ),
                            React.createElement(
                              "div",
                              { className: "text-xs text-gray-400" },
                              `#${task.id}`
                            )
                          ),
                          React.createElement(
                            "p",
                            { className: "mt-2 text-xs text-gray-600" },
                            task.message
                          ),
                          React.createElement(
                            "div",
                            { className: "mt-3 text-xs text-gray-500 space-y-1" },
                            React.createElement(
                              "div",
                              null,
                              React.createElement("strong", null, "Start:"),
                              ` ${task.startDate || "-"}`
                            ),
                            React.createElement(
                              "div",
                              null,
                              React.createElement("strong", null, "End:"),
                              ` ${task.endDate || "-"}`
                            ),
                            React.createElement(
                              "div",
                              null,
                              React.createElement("strong", null, "Raw status:"),
                              ` ${String(task.status)}`
                            )
                          ),
                          React.createElement(
                            "div",
                            { className: "mt-3 flex items-center justify-between" },
                            React.createElement(
                              "div",
                              { className: "text-xs text-gray-500" },
                              "Detalle completo"
                            ),
                            React.createElement(
                              "div",
                              { className: "text-xs" },
                              React.createElement(
                                "button",
                                {
                                  onClick: () => alert(JSON.stringify(task, null, 2)),
                                  className:
                                    "text-sm px-2 py-1 rounded border text-gray-700",
                                },
                                "Ver"
                              )
                            )
                          )
                        )
                      )
                )
              )
            )
          ),
      React.createElement(
        "footer",
        { className: "mt-6 text-xs text-gray-500" },
        "Nota: los cambios son locales en esta demo. Añade persistencia si lo necesitas."
      )
    )
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(React.createElement(TaskBoard));
