import React, { useEffect, useState } from "react";
import ReactApexChart from "react-apexcharts";
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Select, MenuItem } from "@mui/material";

const API_URL = "https://activit.free.beeceptor.com/api/v3/activities";

// Función para convertir fechas
const parseDate = (dateStr) => {
  const [day, month, year] = dateStr.split("-").map(Number);
  return `${year}-${month.toString().padStart(2, "0")}-${day.toString().padStart(2, "0")}`;
};

// Transformar datos para el heatmap
const transformData = (data) => {
  const activityNames = Object.keys(data.activities);
  const uniqueDates = [
    ...new Set(Object.values(data.activities).flatMap((records) => records.map(({ date }) => parseDate(date))))].sort();

  return uniqueDates.map((date) => ({
    name: date,
    data: activityNames.map((activity) => {
      const record = data.activities[activity]?.find((r) => parseDate(r.date) === date);
      let yValue = null;
      if (record) {
        if (record.status === "accomplished") yValue = 1;
        else if (record.status === "fail") yValue = 0;
        else if (record.status === "regular") yValue = 0.5;
        else if (record.status === "suck") yValue = -1; // Asignar un valor para "suck"
        else if (record.status === "excellence") yValue = 2; // Asignar un valor para "excellence"
      }
      return { x: activity, y: yValue, metadata: { date, activity, record } };
    }),
  }));
};

const ApexChart = () => {
  const [chartData, setChartData] = useState({ series: [], options: {} });
  const [modalData, setModalData] = useState(null);
  const [selectedStatus, setSelectedStatus] = useState("");

  useEffect(() => {
    fetch(API_URL)
      .then((response) => response.json())
      .then((data) => {
        const series = transformData(data);
        setChartData({
          series,
          options: {
            chart: {
              height: 9000,
              type: "heatmap",
              events: {
                dataPointSelection: (event, chartContext, { dataPointIndex, seriesIndex }) => {
                  const selectedPoint = series[seriesIndex].data[dataPointIndex];
                  setModalData(selectedPoint.metadata);
                  setSelectedStatus(selectedPoint.metadata.record?.status || "");
                },
              },
            },
            plotOptions: {
              heatmap: {
                shadeIntensity: 0.5,
                radius: 0,
                useFillColorAsStroke: true,
                colorScale: {
                  ranges: [
                    { from: -1, to: -1, name: "Suck", color: "#FF0000" }, // Color para "suck"
                    { from: 0, to: 0, name: "Fail", color: "#FF6600" },
                    { from: 0.5, to: 0.5, name: "Regular", color: "#FFFF00" },
                    { from: 1, to: 1, name: "Accomplished", color: "#00FF00" },
                    { from: 2, to: 2, name: "Excellence", color: "#00A100" } // Color para "excellence"
                  ],
                },
                hover: {
                  opacity: 0.9, // Ajusta la opacidad del hover para que no se pierdan los colores
                },
                stroke: {
                  width: 1,  // El grosor del borde
                  colors: ["#fff"], // Color blanco para el borde
                },
              },
            },
            dataLabels: { enabled: false },
            title: { text: "Activity HeatMap" },
            xaxis: { type: "category", title: { text: "Activities" } },
            yaxis: { title: { text: "Dates" } },
          },
        });
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handleSave = () => {
    console.log("Saving status:", modalData, selectedStatus);
    setModalData(null);
  };

  return (
    <div>
      <h2>Activity Heatmap</h2>
      <ReactApexChart options={chartData.options} series={chartData.series} type="heatmap" height={9000} />

      <Dialog open={!!modalData} onClose={() => setModalData(null)}>
        <DialogTitle>Update Status</DialogTitle>
        <DialogContent>
          <p>Activity: <strong>{modalData?.activity}</strong></p>
          <p>Date: <strong>{modalData?.date}</strong></p>
          <Select fullWidth value={selectedStatus} onChange={(e) => setSelectedStatus(e.target.value)} displayEmpty>
            <MenuItem value="">Select status</MenuItem>
            <MenuItem value="accomplished">Accomplished</MenuItem>
            <MenuItem value="fail">Fail</MenuItem>
            <MenuItem value="regular">Regular</MenuItem>
            <MenuItem value="suck">Suck</MenuItem>
            <MenuItem value="excellence">Excellence</MenuItem>
          </Select>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setModalData(null)} color="secondary">Cancel</Button>
          <Button onClick={handleSave} variant="contained" color="primary">Save</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default ApexChart;
