import React, { useState } from "react";
import Chart from "react-apexcharts";

const WeightChart = () => {
  const [newWeight, setNewWeight] = useState("");
  const [newDate, setNewDate] = useState("");

  const data = {
    ideal: [
      { weight: 84, date: "2025-01-01" },
      { weight: 75, date: "2025-12-31" }
    ],
    current: [
      { weight: 84, date: "2025-01-01" },
      { weight: 83, date: "2025-02-01" },
      { weight: 82, date: "2025-03-01" },
      { weight: 81, date: "2025-04-01" },
      { weight: 80, date: "2025-05-01" },
      { weight: 79, date: "2025-06-01" },
      { weight: 78, date: "2025-07-01" },
      { weight: 77, date: "2025-08-01" },
      { weight: 76, date: "2025-09-01" },
      { weight: 75, date: "2025-10-01" },
      { weight: 74, date: "2025-11-01" },
      { weight: 73, date: "2025-12-01" }
    ]
  };

  const options = {
    chart: {
      id: "weight-chart",
      type: "line",
      zoom: { enabled: false }
    },
    xaxis: {
      type: "datetime",
      title: { text: "Fecha" },
      labels: {
        datetimeFormatter: {
          month: "MMM",
          year: "yyyy"
        }
      }
    },
    yaxis: {
      title: { text: "Peso (kg)" }
    },
    stroke: {
      curve: "smooth"
    },
    markers: {
      size: 4
    },
    legend: {
      show: false
    },
    colors: ["#008FFB", "#00E396"]
  };

  const series = [
    {
      name: "Ideal",
      data: data.ideal.map(d => ({ x: new Date(d.date).getTime(), y: d.weight }))
    },
    {
      name: "Actual",
      data: data.current.map(d => ({ x: new Date(d.date).getTime(), y: d.weight }))
    }
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Nuevo peso:", newWeight, "Fecha:", newDate);
    // lÃ³gica de agregar
  };

  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
      <div style={{ width: "55%", padding: "1rem", backgroundColor: "white", borderRadius: "1rem", boxShadow: "0 4px 10px rgba(0,0,0,0.1)" }}>

        <form onSubmit={handleSubmit}>
          <div style={{ display: "flex", alignItems: "flex-end", justifyContent: "space-between", gap: "1rem", marginBottom: "1rem", flexWrap: "wrap" }}>

            {/* Peso Ideal */}
            <div style={{ flex: 1 }}>
              <div style={{ textAlign: "center", fontWeight: "bold", paddingBottom: "0.5rem" }}>
                Peso Ideal: 75
              </div>
            </div>

            {/* Leyenda */}
            <div style={{ flex: 2, display: "flex", justifyContent: "center", gap: "1rem", paddingBottom: "0.5rem" }}>
              <div style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
                <div style={{ width: "16px", height: "16px", backgroundColor: "#008FFB", borderRadius: "50%" }}></div>
                <span style={{ fontWeight: "bold" }}>Ideal</span>
              </div>
              <div style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
                <div style={{ width: "16px", height: "16px", backgroundColor: "#00E396", borderRadius: "50%" }}></div>
                <span style={{ fontWeight: "bold" }}>Actual</span>
              </div>
            </div>

            {/* Inputs alineados a la derecha */}
            <div style={{ display: "flex", gap: "1rem", flex: 2, justifyContent: "flex-end" }}>
              <input
                type="number"
                value={newWeight}
                onChange={(e) => setNewWeight(e.target.value)}
                placeholder="Nuevo peso (kg)"
                style={{ padding: "0.5rem", borderRadius: "0.5rem", border: "1px solid #ccc", width: "120px" }}
              />
              <input
                type="date"
                value={newDate}
                onChange={(e) => setNewDate(e.target.value)}
                style={{ padding: "0.5rem", borderRadius: "0.5rem", border: "1px solid #ccc" }}
              />
              <button
                type="submit"
                style={{
                  padding: "0.6rem 1.2rem",
                  borderRadius: "0.5rem",
                  border: "none",
                  backgroundColor: "#8e44ad",
                  color: "white",
                  fontWeight: "bold",
                  cursor: "pointer",
                  whiteSpace: "nowrap"
                }}
              >
                Agregar
              </button>
            </div>
          </div>
        </form>

        {/* GrÃ¡fico */}
        <Chart options={options} series={series} type="line" height={300} width="100%" />
      </div>
    </div>
  );
};

export default WeightChart;
