import React, { useEffect, useState } from "react";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
const STATUSES = ["open", "block", "in_progress", "done"];
const STATUS_LABELS = {
  open: "Open",
  block: "Blocked",
  in_progress: "In Progress",
  done: "Done"
};

export default function TodoBoard() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetch("https://testtask.free.beeceptor.com/todo")
      .then((res) => res.json())
      .then((data) => {
        // Normalizamos los estados (por si vienen con otro formato)
        const normalized = data.map((t) => ({
          ...t,
          status:
            t.status === "completed"
              ? "done"
              : t.status === "in_progress"
              ? "in_progress"
              : t.status || "open"
        }));
        setTasks(normalized);
      })
      .catch((err) => console.error("Error fetching tasks:", err));
  }, []);

  const onDragEnd = (result) => {
    if (!result.destination) return;

    const { source, destination } = result;
    // Si se mueve entre columnas
    if (source.droppableId !== destination.droppableId) {
      const updatedTasks = tasks.map((task) =>
        task.id.toString() === result.draggableId
          ? { ...task, status: destination.droppableId }
          : task
      );
      setTasks(updatedTasks);
    }
  };

  const getTasksByStatus = (status) =>
    tasks.filter((task) => task.status === status);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-center">ðŸ“‹ Task Board</h1>

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="grid grid-cols-4 gap-4">
          {STATUSES.map((status) => (
            <Droppable key={status} droppableId={status}>
              {(provided, snapshot) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className={`rounded-lg p-4 min-h-[400px] bg-white shadow-md border-2 transition-all ${
                    snapshot.isDraggingOver ? "border-blue-400" : "border-gray-200"
                  }`}
                >
                  <h2 className="font-semibold text-lg mb-3 text-center text-gray-700">
                    {STATUS_LABELS[status]}
                  </h2>

                  {getTasksByStatus(status).map((task, index) => (
                    <Draggable
                      key={task.id.toString()}
                      draggableId={task.id.toString()}
                      index={index}
                    >
                      {(provided, snapshot) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className={`bg-blue-50 p-3 mb-3 rounded-lg shadow-sm border ${
                            snapshot.isDragging
                              ? "bg-blue-100 border-blue-400"
                              : "border-gray-200"
                          }`}
                        >
                          <h3 className="font-semibold text-gray-800">{task.title}</h3>
                          <p className="text-sm text-gray-600">{task.message}</p>
                          <p className="text-xs text-gray-400 mt-1">
                            {task.startDate} â†’ {task.endDate}
                          </p>
                        </div>
                      )}
                    </Draggable>
                  ))}

                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
}
