import React, { useEffect, useState } from "react";

// TaskBoard.jsx
// Single-file React component (Tailwind CSS) Kanban board with drag & drop
// - Fetches tasks from https://testtask.free.beeceptor.com/todo
// - Columns: Open, Blocked, In Progress, Done
// - Drag cards horizontally between columns (local state only)

const STATUS_ORDER = ["open", "blocked", "in_progress", "done"];
const STATUS_LABEL = {
  open: "Open",
  blocked: "Blocked",
  in_progress: "In Progress",
  done: "Done",
};

function normalizeStatus(raw) {
  if (!raw) return "open";
  const r = raw.toLowerCase();
  if (r === "completed" || r === "done") return "done";
  if (r === "in_progress" || r === "in progress") return "in_progress";
  if (r === "block" || r === "blocked") return "blocked";
  if (r === "open") return "open";
  // fallback
  return r;
}

export default function TaskBoard() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [draggingId, setDraggingId] = useState(null);

  useEffect(() => {
    let mounted = true;
    setLoading(true);
    fetch("https://testtask.free.beeceptor.com/todo")
      .then((r) => {
        if (!r.ok) throw new Error(`HTTP ${r.status}`);
        return r.json();
      })
      .then((data) => {
        if (!mounted) return;
        // normalize tasks and statuses
        const normalized = data.map((t) => ({
          ...t,
          status: normalizeStatus(t.status),
        }));
        setTasks(normalized);
      })
      .catch((e) => setError(e.message))
      .finally(() => mounted && setLoading(false));
    return () => (mounted = false);
  }, []);

  function onDragStart(e, id) {
    e.dataTransfer.setData("text/plain", String(id));
    setDraggingId(id);
    // small effect so the column can style while dragging
  }

  function onDragEnd() {
    setDraggingId(null);
  }

  function allowDrop(e) {
    e.preventDefault();
  }

  function onDrop(e, newStatus) {
    e.preventDefault();
    const id = e.dataTransfer.getData("text/plain");
    if (!id) return;
    setTasks((prev) =>
      prev.map((t) => (String(t.id) === String(id) ? { ...t, status: newStatus } : t))
    );
    setDraggingId(null);

    // OPTIONAL: Persist change to backend here with fetch PATCH/PUT
    // fetch(`https://your.api/tasks/${id}`, { method: 'PATCH', body: JSON.stringify({ status: newStatus }) })
    //   .then(...)
  }

  function tasksByStatus(status) {
    return tasks.filter((t) => t.status === status);
  }

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-7xl mx-auto">
        <header className="mb-6 flex items-center justify-between">
          <h1 className="text-2xl font-semibold">Kanban Board</h1>
          <div className="text-sm text-gray-600">Drag tasks horizontally between columns</div>
        </header>

        {loading ? (
          <div className="py-20 text-center text-gray-500">Cargando tareas...</div>
        ) : error ? (
          <div className="py-20 text-center text-red-600">Error: {error}</div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-4 gap-4">
            {STATUS_ORDER.map((statusKey) => (
              <div
                key={statusKey}
                className={`rounded-lg p-3 border border-gray-200 bg-white flex flex-col max-h-[70vh]`}
                onDragOver={allowDrop}
                onDrop={(e) => onDrop(e, statusKey)}
              >
                <div className="mb-2 flex items-center justify-between">
                  <h2 className="text-sm font-medium">{STATUS_LABEL[statusKey]}</h2>
                  <span className="text-xs text-gray-500">{tasksByStatus(statusKey).length}</span>
                </div>

                <div className="overflow-auto space-y-3">
                  {tasksByStatus(statusKey).length === 0 && (
                    <div className="text-xs text-gray-400">No tasks</div>
                  )}

                  {tasksByStatus(statusKey).map((task) => (
                    <article
                      key={task.id}
                      draggable
                      onDragStart={(e) => onDragStart(e, task.id)}
                      onDragEnd={onDragEnd}
                      className={`p-3 rounded-lg shadow-sm border cursor-grab select-none bg-white" ${
                        draggingId === task.id ? "opacity-60" : "opacity-100"
                      }`}
                    >
                      <div className="flex items-start justify-between">
                        <h3 className="text-sm font-semibold">{task.title}</h3>
                        <div className="text-xs text-gray-400">#{task.id}</div>
                      </div>

                      <p className="mt-2 text-xs text-gray-600">{task.message}</p>

                      <div className="mt-3 text-xs text-gray-500 space-y-1">
                        <div>
                          <strong>Start:</strong> {task.startDate || "-"}
                        </div>
                        <div>
                          <strong>End:</strong> {task.endDate || "-"}
                        </div>
                        <div>
                          <strong>Raw status:</strong> {String(task.status)}
                        </div>
                      </div>

                      <div className="mt-3 flex items-center justify-between">
                        <div className="text-xs text-gray-500">Detalle completo</div>
                        <div className="text-xs">
                          <button
                            onClick={() => alert(JSON.stringify(task, null, 2))}
                            className="text-sm px-2 py-1 rounded border text-gray-700"
                          >
                            Ver
                          </button>
                        </div>
                      </div>
                    </article>
                  ))}
                </div>
              </div>
            ))}
          </div>
        )}

        <footer className="mt-6 text-xs text-gray-500">Nota: los cambios son locales en esta demo. Añade persistencia si lo necesitas.</footer>
      </div>
    </div>
  );
}
